const input = $json.chatInput;

// Use regex to extract BTC and ETH data
const btcMatch = input.match(/Bitcoin: \$([\d.,]+) \(([-\d.]+)% 24h\)/);
const ethMatch = input.match(/Ethereum: \$([\d.,]+) \(([-\d.]+)% 24h\)/);

if (!btcMatch || !ethMatch) {
  throw new Error("BTC/ETH data not found in chatInput");
}

const btc = parseFloat(btcMatch[1].replace(/,/g, ''));
const btcChange = parseFloat(btcMatch[2]);
const eth = parseFloat(ethMatch[1].replace(/,/g, ''));
const ethChange = parseFloat(ethMatch[2]);

function formatChange(val) {
  return (val >= 0 ? '+' : '') + val.toFixed(2);
}

let trend, advice;

if (btcChange > 0 && ethChange > 0) {
  trend = "Both Bitcoin and Ethereum are up in the last 24 hours.";
  advice = "Market is showing bullish signals. Consider monitoring for possible profit-taking opportunities, but remain cautious of sudden reversals.";
} else if (btcChange < 0 && ethChange < 0) {
  trend = "Both Bitcoin and Ethereum have declined in the last 24 hours.";
  advice = "Bearish momentum detected. Consider risk management strategies or waiting for a clearer trend before making new investments.";
} else if (btcChange > 0 && ethChange < 0) {
  trend = "Bitcoin is up, but Ethereum is down in the last 24 hours.";
  advice = "The market is mixed. Diversification or watching ETH for a reversal could be wise.";
} else if (btcChange < 0 && ethChange > 0) {
  trend = "Bitcoin is down, but Ethereum is up in the last 24 hours.";
  advice = "The market is mixed. Monitor BTC for support or ETH for sustained strength.";
} else {
  trend = "The market is relatively flat today.";
  advice = "Patience may be key; consider waiting for clearer trends before acting.";
}

const summary = `
BTC: $${btc} (${formatChange(btcChange)}% 24h)
ETH: $${eth} (${formatChange(ethChange)}% 24h)
Trend: ${trend}
Tip: ${advice}
`.trim();

return {
  btc,
  btcChange,
  eth,
  ethChange,
  summary
};

const chartConfig = {
  type: 'line',
  data: {
    labels: $json.chartLabels,
    datasets: [
      {
        label: 'BTC',
        borderColor: 'orange',
        fill: false,
        data: $json.chartBTC,
      },
      {
        label: 'ETH',
        borderColor: 'purple',
        fill: false,
        data: $json.chartETH,
      }
    ]
  },
  options: {
    title: {
      display: true,
      text: 'BTC & ETH ‚Äì Last 7 Days'
    }
  }
};

return [{
  json: {
    data: 'https://quickchart.io/chart?c=' + encodeURIComponent(JSON.stringify(chartConfig))
  }
}];

let crypto = null, chart = null, weather = null, weatherChart = null;

// Identify the crypto, chart, and weather JSON objects
for (const item of items) {
  if ("BTC" in item.json && "ETH" in item.json) crypto = item.json;
  else if ("data" in item.json) chart = item.json;
  else if ("weather5d" in item.json) weather = item.json;
  else if ("weatherChartUrl" in item.json) weatherChart = item.json;
}

// Defaults
const city = weather ? weather.City || "" : "";
const country = weather ? weather.Country || "" : "";

// Extract today's and tomorrow's forecast for highlight
let dateTime = "", temperature = "", condition = "";
if (weather && Array.isArray(weather.weather5d) && weather.weather5d.length > 0) {
  dateTime = weather.weather5d[0].date || "";
  temperature = weather.weather5d[0].temp || "";
  condition = weather.weather5d[0].condition || "";
}

// --- Helpers ---
function prettyDate(isoString) {
  if (!isoString) return "";
  const date = new Date(isoString.replace(" ", "T"));
  return date.toLocaleDateString("en-US", { month: "short", day: "numeric", year: "numeric" });
}
function money(n) {
  return Number(n).toLocaleString(undefined, {minimumFractionDigits:2, maximumFractionDigits:2});
}
function weatherIcon(condition) {
  const c = (condition || "").toLowerCase();
  if (c.includes("rain")) return "üåßÔ∏è";
  if (c.includes("cloud")) return "‚òÅÔ∏è";
  if (c.includes("clear")) return "‚òÄÔ∏è";
  return "üå°Ô∏è";
}

// ---- Date for Title and Subject ----
// Get today's date in "October 30, 2025" format
const todayPretty = new Date().toLocaleDateString("en-US", { month: "long", day: "numeric", year: "numeric" });

// --- Section Render Functions ---

function renderCryptoSnapshot(crypto) {
  if (!crypto) return "<p>No crypto data.</p>";
  const btc = Number(crypto.BTC);
  const btcChange = parseFloat(crypto["BTC Change"]);
  const eth = Number(crypto.ETH);
  const ethChange = parseFloat(crypto["ETH Change"]);

  // Trend logic
  let trendText = "";
  let tip = "";
  if (btcChange < 0 && ethChange < 0) {
    trendText = "Both Bitcoin and Ethereum have declined in the last 24 hours.";
    tip = "Bearish momentum detected. Consider risk management strategies or waiting for a clearer trend before making new investments.";
  } else if (btcChange > 0 && ethChange > 0) {
    trendText = "Both Bitcoin and Ethereum have appreciated in the last 24 hours.";
    tip = "Bullish momentum! Consider scaling in or reviewing your take-profit levels, but always manage risk.";
  } else {
    trendText = "Bitcoin and Ethereum have moved in different directions in the last 24 hours.";
    tip = "Mixed signals. Monitor market news and be prepared for volatility.";
  }
  return `
    <h3 style="color:#1867c0; margin-bottom:8px; margin-top:0;">Crypto Snapshot</h3>
    <ul style="margin-top:0;margin-bottom:10px;">
      <li>Bitcoin: $${money(btc)} (${btcChange.toFixed(2)}% 24h)</li>
      <li>Ethereum: $${money(eth)} (${ethChange.toFixed(2)}% 24h)</li>
    </ul>
    <div style="margin-bottom:8px;">
      BTC: $${money(btc)} (${btcChange.toFixed(2)}% 24h) &nbsp; 
      ETH: $${money(eth)} (${ethChange.toFixed(2)}% 24h)
    </div>
    <div style="margin:8px 0 0 0;"><b>Trend:</b> ${trendText}</div>
    <div style="color:#417505; margin:4px 0 0 0;"><b>Tip:</b> ${tip}</div>
  `;
}

function renderCryptoChart(chart) {
  if (!chart || !chart.data) return "";
  return `
    <h3 style="color:#1867c0; margin-bottom:8px; margin-top:16px;">Crypto 7-Day Chart</h3>
    <div>
      <img src="${chart.data}" alt="Crypto 7-Day Chart" style="max-width:600px; width:100%; border:1px solid #eee; border-radius:7px;">
    </div>
  `;
}

// -- Weather section only a subtle location label! --
function renderWeatherForecast(weather, weather5dHtml) {
  if (!weather) return "<p>No weather data.</p>";
  return `
    <div style="color:#555; margin-bottom:8px; font-size:1em;">
      Location: ${weather.City}, ${weather.Country}
    </div>
    ${weather5dHtml}
  `;
}

function renderWeatherChart(weatherChart) {
  if (!weatherChart || !weatherChart.weatherChartUrl) return "";
  return `
    <h3 style="color:#1867c0; margin-bottom:8px; margin-top:16px;">5-Day Temperature Graph</h3>
    <div>
      <img src="${weatherChart.weatherChartUrl}" alt="5-Day Weather Chart" style="max-width:600px; width:100%; border:1px solid #eee; border-radius:7px;">
    </div>
  `;
}

function renderSummary(fullSummary) {
  return `
    <h3 style="color:#1867c0; margin-bottom:8px; margin-top:16px;">Summary</h3>
    <div style="white-space:pre-line; font-family:inherit;">
      ${fullSummary}
    </div>
  `;
}

// --- SUMMARY TEXTS ---
const cryptoSummary = crypto
  ? `Crypto:\n‚Ä¢ Bitcoin: $${money(crypto.BTC)} (${parseFloat(crypto["BTC Change"]).toFixed(2)}% 24h)\n‚Ä¢ Ethereum: $${money(crypto.ETH)} (${parseFloat(crypto["ETH Change"]).toFixed(2)}% 24h)`
  : "Crypto data missing.";

const weatherSummary = (weather && Array.isArray(weather.weather5d) && weather.weather5d[0])
  ? `Weather for ${city}${country ? ', ' + country : ''} on ${prettyDate(weather.weather5d[0].date)}:\n‚Ä¢ ${parseFloat(weather.weather5d[0].temp).toFixed(1)} ¬∞C, ${weather.weather5d[0].condition} ${weatherIcon(weather.weather5d[0].condition)}`
  : "Weather data missing.";

const fullSummary = cryptoSummary + "\n\n" + weatherSummary;

// --- Weather Table with Highlights ---
let weather5dHtml = '';
if (weather && Array.isArray(weather.weather5d)) {
  weather5dHtml = `<table border="1" cellpadding="6" cellspacing="0" style="border-collapse:collapse; min-width:320px;"><thead>
      <tr style="background:#f2f2f2;">
        <th>Date</th>
        <th>Temperature (¬∞C)</th>
        <th>Condition</th>
      </tr>
    </thead><tbody>`;
  for (let i = 0; i < weather.weather5d.length; i++) {
    const day = weather.weather5d[i];
    let label = prettyDate(day.date);
    if (i === 0) label = "<b>Today</b> (" + prettyDate(day.date) + ")";
    if (i === 1) label = "<b>Tomorrow</b> (" + prettyDate(day.date) + ")";
    let style = (i === 0 || i === 1)
      ? 'font-weight:bold;background:#e6f7ff;'
      : '';
    weather5dHtml += `<tr style="${style}">` +
      `<td>${label}</td>` +
      `<td style="text-align:center;">${parseFloat(day.temp).toFixed(1)}</td>` +
      `<td>${day.condition} ${weatherIcon(day.condition)}</td>` +
      `</tr>`;
  }
  weather5dHtml += '</tbody></table>';
}

// ---- Final HTML message assembled ----
const htmlMessage = `
<div style="max-width: 640px; margin: auto; font-family: Arial, sans-serif; color: #222;">
  <h2 style="margin-bottom:8px;">Crypto &amp; Weather Dashboard for ${todayPretty}</h2>
  <hr style="border:none; border-top:2px solid #eee; margin-bottom:24px;">

  <div style="margin-bottom:24px;">
    ${renderCryptoSnapshot(crypto)}
  </div>
  <div style="margin-bottom:24px;">
    ${renderCryptoChart(chart)}
  </div>

  <!-- PREMIUM DATA SEPARATOR AS SECTION HEADER -->
  <div style="margin:32px 0 32px 0; text-align:center; display:flex; align-items:center;">
    <div style="flex:1; border-top:2px solid #e0e0e0;"></div>
    <h3 style="flex:0 0 auto; margin:0 16px; color:#1867c0; font-weight:700; letter-spacing:1px; font-size:1.17em; line-height:1;">
      üì∞ Weather Data Report
    </h3>
    <div style="flex:1; border-top:2px solid #e0e0e0;"></div>
  </div>
  <!-- /PREMIUM DATA SEPARATOR -->

  <div style="margin-bottom:24px;">
    ${renderWeatherForecast(weather, weather5dHtml)}
  </div>
  <div style="margin-bottom:24px;">
    ${renderWeatherChart(weatherChart)}
  </div>
  <div style="margin-bottom:24px;">
    ${renderSummary(fullSummary)}
  </div>

  <div style="margin-top:32px; font-size:13px; color:#888;">
    <em>This email was sent automatically with n8n ‚Äì <a href="https://n8n.io" style="color:#888;" target="_blank">n8n.io</a></em>
  </div>
</div>
`;

// --- Final result object ---
return [
  {
    json: {
      ...(crypto || {}),
      ...(weather || {}),
      ...(chart || {}),
      ...(weatherChart || {}),
      DateTime: dateTime,
      Temperature: temperature,
      Weather: condition,
      FullSummary: fullSummary,
      Weather5dHtml: weather5dHtml,
      HtmlMessage: htmlMessage,
      ReportDate: todayPretty // optional: for email subject if needed
    }
  }
];

const labels = [];
const btc = [];
const eth = [];

// Reverse for chronological order, take ONLY last 7 entries
const input = items.reverse().slice(0, 7);

for (const item of input) {
  // Example Date: "October Oct 29, 2025, YYYY 11:49"
  let dateRaw = (item.json.Date || '').trim();
  let label = dateRaw; // fallback

  // Try to extract short month and day via regexp
  // Matches e.g. "October Oct 29, 2025"
  // Groups: [1]=long month, [2]=short month, [3]=day (number)
  const match = dateRaw.match(/^[A-Za-z]+\s([A-Za-z]+)\s(\d+),/);

  if (match) {
    label = `${match[1]} ${match[2]}`; // e.g. "Oct 29"
  }

  labels.push(label);
  btc.push(Number(item.json.BTC));
  eth.push(Number(item.json.ETH));
}

return [{
  json: {
    chartLabels: labels,
    chartBTC: btc,
    chartETH: eth
  }
}];

// 1. Extract forecast entries and city info
const list = items[0].json.list || [];
const cityObj = items[0].json.city || {};
const cityName = cityObj.name ?? "";
const country = cityObj.country ?? "";

// 2. Group entries by day (YYYY-MM-DD)
const days = {}; // { '2025-10-31': [entry, entry, ...], ... }
for (const entry of list) {
  const date = entry.dt_txt;
  if (!date) continue;
  const day = date.split(' ')[0];
  if (!days[day]) days[day] = [];
  days[day].push(entry);
}

// 3. For each day, select "15:00:00" entry, or else first available
const sortedDays = Object.keys(days).sort();
const results = [];
for (const day of sortedDays) {
  const entries = days[day];
  // Find "15:00:00" if it exists, else take first
  const pick = entries.find(e => e.dt_txt.endsWith("15:00:00")) || entries[0];
  results.push({
    date: pick.dt_txt,
    temp: pick.main.temp,
    condition: pick.weather[0].description
  });
  if (results.length >= 5) break;
}

// 4. Assemble output
return [
  {
    json: {
      City: cityName,
      Country: country,
      weather5d: results
    }
  }
];

const days = $json.weather5d || [];
const labels = days.map(d => {
  const dt = new Date(d.date.replace(' ', 'T'));
  return dt.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' });
});
const temps = days.map(d => parseFloat(d.temp));

const chartConfig = {
  type: "line",
  data: {
    labels: labels,
    datasets: [{
      label: "Temperature (¬∞C)",
      data: temps,
      borderColor: "rgba(44,123,204,1)",
      backgroundColor: "rgba(44,123,204,0.15)",
      fill: true,
      pointRadius: 5,
      pointBackgroundColor: "rgba(44,123,204,1)"
    }]
  },
  options: {
    title: { display: true, text: "5-Day Temperature Forecast" },
    legend: { display: false },
    scales: {
      y: { beginAtZero: false }
    }
  }
};

return [{
  json: {
    weatherChartUrl: 'https://quickchart.io/chart?c=' + encodeURIComponent(JSON.stringify(chartConfig))
  }
}];
